var data = require('data'),
    utils = require('utils');

exports.handlerGet = function (req, res) {
  res.set("Content-Type", "application/json");
  data.getData(req.params.calcName, function(retData) {
    res.send(retData);
  });
};

exports.handlerPost = function (req, res) {
  res.set("Content-Type", "application/json");
  try {
    data.saveData(JSON.parse(req.body.calcData), function(retData) {
      res.send(retData);
    });
  }
  catch(err) {
    res.send(utils.retError(err));
  }
};
