exports.retStatus = function(status) {
  return JSON.stringify({
    status : status,
  });
};

exports.retResult = function(result) {
  return JSON.stringify({
    status : "Success",
    result : result,
  });
};

exports.retError = function(errorStr) {
  return JSON.stringify({
    status : "Error",
    error : errorStr,
  });
};
